<#\n.SYNOPSIS\n    EngageDoc-Studio v209 デバッグモード起動スクリプト\n    \n.DESCRIPTION\n    開発者向けのデバッグ機能を有効にしてアプリケーションを起動します。\n    詳細ログ、開発者ツール、パフォーマンス監視機能を含みます。\n#>\n\n[CmdletBinding()]\nparam(\n    [switch]$Verbose,\n    [switch]$Performance,\n    [switch]$Memory\n)\n\n$ScriptPath = Split-Path -Parent $MyInvocation.MyCommand.Path\n$StartScript = Join-Path $ScriptPath \"EngageDoc-Studio-Start.ps1\"\n\nfunction Show-DebugInfo {\n    Write-Host \"\"\n    Write-Host \"===========================================\" -ForegroundColor Red\n    Write-Host \"  EngageDoc-Studio v209 DEBUG MODE       \" -ForegroundColor Red\n    Write-Host \"===========================================\" -ForegroundColor Red\n    Write-Host \"\"\n    Write-Host \"Debug Features Enabled:\" -ForegroundColor White\n    Write-Host \"  • Verbose logging\" -ForegroundColor Gray\n    Write-Host \"  • Developer Tools\" -ForegroundColor Gray\n    Write-Host \"  • Hot reload\" -ForegroundColor Gray\n    Write-Host \"  • Performance monitoring\" -ForegroundColor Gray\n    Write-Host \"  • Memory usage tracking\" -ForegroundColor Gray\n    Write-Host \"  • ClaudeCode history sync monitoring\" -ForegroundColor Yellow\n    Write-Host \"\"\n    Write-Host \"Press Ctrl+C to stop\" -ForegroundColor Red\n    Write-Host \"\"\n}\n\n# デバッグ環境変数設定\n$env:NODE_ENV = \"development\"\n$env:ELECTRON_ENABLE_LOGGING = \"true\"\n$env:DEBUG = \"engagedoc:*,electron:*\"\n$env:ELECTRON_ENABLE_STACK_DUMPING = \"true\"\n$env:ELECTRON_ENABLE_HEAP_PROFILING = \"true\"\n\nif ($Verbose) {\n    $env:DEBUG = \"*\" # 全てのデバッグログを有効\n}\n\nif ($Performance) {\n    $env:ENABLE_PERFORMANCE_MONITORING = \"true\"\n}\n\nif ($Memory) {\n    $env:ENABLE_MEMORY_MONITORING = \"true\"\n}\n\ntry {\n    Show-DebugInfo\n    \n    # デバッグモードで起動スクリプトを呼び出し\n    & $StartScript -Debug -DevTools\n    \n} finally {\n    # 環境変数クリーンアップ\n    Remove-Item Env:\\NODE_ENV -ErrorAction SilentlyContinue\n    Remove-Item Env:\\ELECTRON_ENABLE_LOGGING -ErrorAction SilentlyContinue\n    Remove-Item Env:\\DEBUG -ErrorAction SilentlyContinue\n    Remove-Item Env:\\ELECTRON_ENABLE_STACK_DUMPING -ErrorAction SilentlyContinue\n    Remove-Item Env:\\ELECTRON_ENABLE_HEAP_PROFILING -ErrorAction SilentlyContinue\n    Remove-Item Env:\\ENABLE_PERFORMANCE_MONITORING -ErrorAction SilentlyContinue\n    Remove-Item Env:\\ENABLE_MEMORY_MONITORING -ErrorAction SilentlyContinue\n}